<?phpnamespace Training\T03MessageSenderReceiver;use PhpAmqpLib\Connection\AMQPStreamConnection;use PhpAmqpLib\Message\AMQPMessage;class QuotationConsumer{    public function __invoke()    {        $connection = new AMQPStreamConnection('rabbitmq', 5672, 'guest', 'guest');        $channel = $connection->channel();        $callback = function () {            call_user_func_array([$this, 'processMessage'], func_get_args());        };        $channel->basic_consume("quotations", "", false, true, false, false, $callback);        while (count($channel->callbacks)) {            $channel->wait();        }    }    private function processMessage(AMQPMessage $message)    {        echo "Receive this message : " . $message->getBody() . PHP_EOL;    }}